require("dotenv").config();
const express = require("express");
const bodyParser = require("body-parser");
const cors = require("cors");

const app = express();
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({
  extended: false
}));

app.use(cors());

app.get("/recipes", (req, res, next) => {
  const recipes = [{
      id: "0",
      name: "Rice",
      description: "A healthy choice!",
      imagePath: "data:image/jpeg;base64,",
      ingredients: [{
        name: 'Rice',
        amount: 6
      }]
    },
    {
      id: "1",
      name: "Pizza",
      description: "Everybody knows Pizza!",
      imagePath: "data:image/jpeg;base64,",
      ingredients: [{
        name: 'Bread',
        amount: 6
      }, {
        name: 'Cheese',
        amount: 12
      }]
    },
    {
      id: "2",
      name: "Hamburger",
      description: "A delicious Fast Food",
      imagePath: "data:image/jpeg;base64,",
      ingredients: [{
        name: "Meat",
        amount: 6
      }, {
        name: "Bread",
        amount: 2
      }]
    }
  ];

  res.status(200).json({
    message: "Success",
    recipes: recipes
  });
});

app.post("/recipes", (req, res, next) => {
  const recipe = req.body;
  console.log(recipe);

  res.status(201).json({
    message: "Recipe added sucessfully"
  });
});

module.exports = app;
