import { Component, OnInit, Output, EventEmitter } from '@angular/core';
import { Recipe } from '../recipe.model';

@Component({
  selector: 'app-recipe-list',
  templateUrl: './recipe-list.component.html',
  styleUrls: ['./recipe-list.component.css']
})
export class RecipeListComponent implements OnInit {
  recipes: Recipe[] =[
    new Recipe("Hamburger", "A delicious Fast Food", "data:image/jpeg;base64,"),
    new Recipe("Hamburger2", "A delicious Fast Food2", "data:image/jpeg;base64,")
  ];
  @Output() recipeDetails = new EventEmitter<Recipe>();

  constructor() { }

  ngOnInit(): void {
  }

  onPassingRecipeDetails(recipe: Recipe ){
    this.recipeDetails.emit(recipe);
  }
}
